<?php/* @var $this yii\web\View *//* @var $form yii\bootstrap4\ActiveForm *//* @var $model \frontend\models\VerifyForm */use common\widgets\Alert;use yii\bootstrap4\ActiveForm;use yii\helpers\Html;$this->title = Yii::t('app', 'Forgot your password?');$this->params['breadcrumbs'][] = $this->title;?><style type="text/css">    .alert-dismissible .close {        padding: 7px 14px;    }</style><section class="sign-up sms">    <div class="container">        <div class="row justify-content-center">            <div class="sign-up-body">                <?= Alert::widget() ?>                <h2 class="title"><?= Yii::t('app', 'update_password') ?></h2>                <p class="subtitle"><?= Yii::t('app', 'write_sms') ?></p>                <?php $form = ActiveForm::begin(['id' => 'form-verify', 'class' => 'sms-form']); ?>                    <?= $form->field($model, 'code', ['options' => ['style' => 'margin-bottom: 0;']])->textInput(['class' => '', 'style' => 'margin-bottom: 5px;']) ?>                    <?= Html::a(Yii::t('app', 'Resend code'), [                            '/site/verify-phone-number',                            'login' => $login,                            'phoneMask' => $phoneMask,                            'flag' => 'yes'                        ], [                            'style' => 'margin-bottom: 25px; display: block;'                        ]) ?>                    <?= $form->field($model, 'password')->passwordInput(['class' => '']) ?>                    <?= $form->field($model, 'repassword')->passwordInput(['class' => '']) ?>                    <button type="submit" class="submit-btn"><?= Yii::t('app', 'Submit') ?></button>                <?php ActiveForm::end(); ?>            </div>        </div>    </div></section>