<?phpuse common\widgets\Alert;use yii\bootstrap4\ActiveForm;$this->title = Yii::t('app', 'sms_code');$this->params['breadcrumbs'][] = $this->title;?><section class="sign-up sms">	<div class="container">		<div class="row justify-content-center">			<div class="sign-up-body">				<?= Alert::widget() ?>				<h2 class="title"><?= Yii::t('app', 'sms_code') ?></h2>				<p class="subtitle"><?= Yii::t('app', 'write_sms') ?></p>				<?php $form = ActiveForm::begin(['id' => 'form-verify', 'class' => 'sms-form']); ?>					<?= $form->field($model, 'number')->textInput(['class' => '']) ?>					<?= $form->field($model, 'captcha')->widget(\yii\captcha\Captcha::className(), [						'options' => [							'class' => '',							'style' => 'margin-bottom: 0;'						],						'template' => '<div class="row"><div class="col-lg-4" style="padding-right: 0; padding-top: 10px; margin-right: -20px;">{image}</div><div class="col-lg-8" style="padding: 0;">{input}</div></div>',					])->label(false)->hint(Yii::t('app', 'Hint: click on the equation to refresh')) ?>					<button type="submit" class="submit-btn"><?= Yii::t('app', 'login') ?></button>				<?php ActiveForm::end(); ?>			</div>		</div>	</div></section>